#renderArea {
  width: 600px;
  height: 600px;
  position: relative;
}
#img {
  width: 224px;
  height: 224px;
  position: absolute;
  top: 190px;
  left: 190px;
  z-index: 10;
  transform: scale(0.3, 0.3);
  -ms-transform: scale(0.3, 0.3);
  -webkit-transform: scale(0.3, 0.3);
}
.hidden {
  display: none;
}
.container{
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
}
#imgChooserContainer{
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: start;
}
#orLabel{
  margin-left: 20px;
  margin-right: 20px
}
#loader {
    border: 30px solid #f3f3f3; /* Light grey */
    border-top: 30px solid #3498db; /* Blue */
    border-radius: 50%;
    width: 300px;
    height: 300px;
    animation: spin 2s linear infinite;
    margin-bottom: 20px;
}

@keyframes spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
}
